import React, { useEffect, useState } from "react";
import axios from "axios";

function Shop() {
const [products, setProducts] = useState([]);
const [error, setError] = useState(null);

useEffect(() => {
const fetchProducts = async () => {
console.log("üõçÔ∏è Chargement des produits...");
try {
const res = await axios.get("/.netlify/functions/products");
setProducts(res.data);
console.log("‚úÖ Produits charg√©s :", res.data);
} catch (err) {
console.error("‚ùå Erreur backend :", err.message);
setError("Impossible de charger les produits pour le moment.");
}
};
fetchProducts();
}, []);

return (
<div style={{ padding: "2rem" }}>
<h1>üõçÔ∏è Boutique Privat Label</h1>
{error ? (
<p style={{ color: "red" }}>{error}</p>
) : products.length > 0 ? (
<div style={{
display: "grid",
gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
gap: "1.5rem",
marginTop: "2rem"
}}>
{products.map((p) => (
<div
key={p.id}
style={{
background: "rgba(255,255,255,0.05)",
borderRadius: "12px",
padding: "1rem",
boxShadow: "0 0 10px rgba(255, 105, 180, 0.3)"
}}
>
<img
src={p.image}
alt={p.name}
style={{ width: "100%", borderRadius: "8px" }}
/>
<h3>{p.name}</h3>
<p>{p.price} ‚Ç¨</p>
</div>
))}
</div>
) : (
<p>Chargement des produits...</p>
)}
</div>
);
}

export default Shop;
